mapper.select:
SELECT COUNT(1) > 0
FROM t_category
WHERE name = #{name, jdbcType=VARCHAR}

SELECT COUNT(1) > 0
FROM t_category
WHERE name = #{name, jdbcType=VARCHAR}
AND id != #{excludeId, jdbcType=BIGINT}

mapper.update:
UPDATE t_category
SET article_count = #{count, jdbcType=INTEGER}
WHERE id = #{id, jdbcType=BIGINT}

UPDATE t_category
SET article_count = IFNULL(article_count, 0) + 1
WHERE id = #{id, jdbcType=BIGINT}

UPDATE t_category
SET article_count = GREATEST(IFNULL(article_count, 0) - 1, 0)
WHERE id = #{id, jdbcType=BIGINT}
