mapper.select:
SELECT c.*, u.username, u.nickname, u.avatar FROM t_comment c LEFT JOIN t_user u ON c.user_id = u.id WHERE c.article_id = #{articleId}
AND c.status = #{status}
AND c.parent_id IS NULL ORDER BY c.create_time DESC

SELECT c.*, u.username, u.nickname, u.avatar FROM t_comment c LEFT JOIN t_user u ON c.user_id = u.id WHERE c.article_id = #{articleId}
AND c.status = #{status}
AND c.parent_id IS NULL ORDER BY c.create_time DESC

SELECT c.*, u.username, u.nickname, u.avatar
FROM t_comment c
LEFT JOIN t_user u ON c.user_id = u.id
WHERE c.parent_id = #{parentId}

    AND c.status = #{status}

ORDER BY c.create_time ASC

SELECT c.*, u.username, u.nickname, u.avatar
FROM t_comment c
LEFT JOIN t_user u ON c.user_id = u.id
WHERE c.user_id = #{userId}

    AND c.status = #{status}

ORDER BY c.create_time DESC

    LIMIT #{limit}

mapper.update:
UPDATE t_comment
SET like_count = IFNULL(like_count, 0) + 1
WHERE id = #{id}

UPDATE t_comment
SET like_count = GREATEST(IFNULL(like_count, 0) - 1, 0)
WHERE id = #{id}

UPDATE t_comment
SET status = #{status}
WHERE id = #{id}
