mapper.select:
SELECT * FROM t_comment_report


        AND status = #{status}


ORDER BY create_time DESC

SELECT * FROM t_comment_report
WHERE comment_id = #{commentId}
ORDER BY create_time DESC

SELECT * FROM t_comment_report
WHERE reporter_id = #{reporterId}
ORDER BY create_time DESC

SELECT * FROM t_comment_report
WHERE create_time >= #{startTime} AND create_time <= #{endTime}
ORDER BY create_time DESC
