mapper.select:
SELECT COUNT(1) > 0
FROM t_tag
WHERE name = #{name}

SELECT COUNT(1) > 0
FROM t_tag
WHERE name = #{name} AND id != #{excludeId}

mapper.update:
UPDATE t_tag
SET article_count = #{count}
WHERE id = #{id}

UPDATE t_tag
SET article_count = article_count + 1
WHERE id = #{id}

UPDATE t_tag
SET article_count = GREATEST(article_count - 1, 0)
WHERE id = #{id}
